mapper.select:
SElECT product_id, product_name, product_desc,product_img,normal_price, promotion_price,priority,enable_status,product_category_id,create_time,last_edit_time FROM product
AND product_id is not null
AND product_category_id = #{productCondition.productCategory.productCategoryId}
AND product_id = #{productCondition.productId}
AND product_Name like '%${productCondition.productName}%'
AND enable_status = 1 ORDER BY priority DESC
